import pandas as pd


def add_index_curve_level(df: pd.DataFrame, curve: str) -> pd.DataFrame:
    """
    Add a new index level to the dataframe converting it into a multi-index dataframe
    and swap the levels accordingly.

    Parameters
    ----------
    df : pd.DataFrame
        The DataFrame whose columns need to be adjusted.
    curve : str
        The name of the level to be used to create the new level.

    Returns
    -------
    pd.DataFrame
        DataFrame with adjusted multi-index columns.
    """

    old_index = df.columns
    old_index_df = old_index.to_frame()
    old_index_df.insert(0, "curve", len(old_index) * [curve])
    new_index_names = list(old_index.names)
    new_index_names.insert(0, "curve")
    new_index = pd.MultiIndex.from_frame(old_index_df, names=new_index_names)
    df.columns = new_index

    for i in range(len(df.columns.names) - 1):
        df = df.swaplevel(i, i + 1, axis=1)

    return df
