import pandas as pd
from .nonnull_quantification import row_wise_nonnull_quantification


def test_nonnull_quantification():
    data = pd.DataFrame({"A": [1, 2, None, 4], "B": [None, 2, 3, 4], "C": [1, 2, 1, 4]})
    total_inverters = 3
    assert row_wise_nonnull_quantification(data, total_inverters) == [
        0.6666666666666666,
        1.0,
        0.6666666666666666,
        1.0,
    ]
