import pandas as pd


def get_next_n_timestamp_indexes(
    timestamp: pd.Timestamp, data_freq: int, periods: int
) -> list:
    """
    Returns the next N (i.e. periods) timestamps after the "timestamp".

    Parameters:
    timestamp : pd.Timestamp
        Timestamp.
    data_freq : int
        The frequency of the timestamps in minutes.
    periods : int
        Number of timestamps periods to be returned.

    Returns:
    next_n_timestamps : list of datetime.datetime
        Next N timestamps.

    Raises:
        ValueError: If timestamp is not a pandas Timestamp object.
        ValueError: If data_freq is not an integer.
        ValueError: If periods is not an integer.
    """

    if not isinstance(timestamp, pd.Timestamp):
        raise ValueError("timestamp must be a pandas Timestamp object")
    if not isinstance(data_freq, int):
        raise ValueError("data_freq must be an integer")
    if not isinstance(periods, int):
        raise ValueError("periods must be an integer")

    next_n_timestamps = pd.date_range(
        start=timestamp, periods=periods + 1, freq=f"{data_freq}T"
    )
    return next_n_timestamps[1:]
